package com.rainbow.main.widget;

import java.util.HashMap;
import java.util.List;

import com.rainbow.main.R;
import com.rainbow.main.RainBowApplication;
import android.annotation.SuppressLint;
import android.content.Context;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.view.View.OnClickListener;
import android.widget.BaseAdapter;
import android.widget.ImageView;
import android.widget.LinearLayout;
import android.widget.TextView;

public class SiteSecAdapter extends BaseAdapter {

	private LayoutInflater mInflater;
	private List<HashMap<String, Object>> mList;
	private int mResource;
	private int[] mTo;
    private String[] mFrom;
    private OnClickListener mOnClickListener;
    private RainBowApplication mBGApplication;
    private String TAG;
    private int mptposition;
    Context mContext;
	LayoutInflater inflater;
    public SiteSecAdapter(Context context,RainBowApplication mBGApplication,String tag,OnClickListener onClickListener,int ptposition, List<HashMap<String, Object>> items, 
			int resource) {
    	mOnClickListener =  onClickListener;
		mInflater = (LayoutInflater) context.getSystemService(Context.LAYOUT_INFLATER_SERVICE);
		mList = items;
        this.mptposition = ptposition;
        this.mContext=context;
		mResource = resource;
		this.mBGApplication= mBGApplication;
		this.TAG =tag;
		inflater = LayoutInflater.from(context);
	}
	
	
    @Override
	public int getCount() {
		return mList.size();
	}


	@Override
	public Object getItem(int position) {
		return mList.get(position);
	}


	@Override
	public long getItemId(int position) {
		return position;
	}

	@SuppressLint("NewApi")
	@Override                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
	public View getView(int position, View convertView, ViewGroup parent) {
		ViewHolder holder = null;
		if(null == convertView){
			holder = new ViewHolder();
			convertView = inflater.inflate(R.layout.site_des_item_chird, null);
			holder.tv_detail_name = (TextView) convertView.findViewById(R.id.tv_detail_name);
			holder.tv_state = (ImageView) convertView.findViewById(R.id.tv_state);
			holder.ll_site = (LinearLayout) convertView.findViewById(R.id.ll_site);
			convertView.setTag(holder);
		}else{
			holder = (ViewHolder) convertView.getTag();
		}
		
		 holder.tv_detail_name.setText(mList.get(position).get("dform_measptname") + "");
		 holder.ll_site.setTag(mptposition+"="+position);
		 holder.ll_site.setOnClickListener(mOnClickListener);
		 if((mList.get(position).get("state_color")+"").equals("1")){
			 holder.tv_state.setImageDrawable(mContext.getResources().getDrawable(R.drawable.icon_red));
		 }else{
			 holder.tv_state.setImageDrawable(mContext.getResources().getDrawable(R.drawable.icon_green));
		 }
		 
		return convertView;
	}

	
	class ViewHolder{
		TextView tv_detail_name;
		ImageView tv_state;
		LinearLayout ll_site;
	}
}
